sql.where:
and username=#{username}
and password=#{password}

and state = 1

mapper.select:
select * from user

    and username=#{username}
    and password=#{password}

    and state = 1

mapper.update:
update user
username=#{username,jdbcType=VARCHAR}, password=#{password,jdbcType=VARCHAR}, telephone=#{TelePhone,jdbcType=VARCHAR}, birthday=#{Birthday,jdbcType=TIMESTAMP}, sex=#{Sex,jdbcType=VARCHAR}, Email=#{Email,jdbcType=VARCHAR}
where userid = #{userid};
