username=#{username,jdbcType=VARCHAR}, password=#{password,jdbcType=VARCHAR}, telephone=#{tephone,jdbcType=VARCHAR}, birthday=#{birthday,jdbcType=TIMESTAMP}, sex=#{sex,jdbcType=VARCHAR}, Email=#{email,jdbcType=VARCHAR}
where userid = #{userid};
